INSERT INTO `cor_article` (`article_id`, `ac_id`, `article_url`, `article_show`, `article_sort`, `article_title`, `article_content`, `article_time`) VALUES
(52, 1, '', 1, 255, 'B2B2C电商平台商城免费开店，现火热招商中!', '<p class="MsoNormal" style="text-indent:24.1pt;">\r\n	<b><span style="font-size:16px;color:#000000;">关于B2B2C电商平台</span><span></span></b> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<b><span style="font-size:16px;color:#000000;">B2B2C电商平台</span></b><span style="font-size:16px;color:#000000;">是同城</span><span style="font-size:16px;color:#000000;">020</span><span style="font-size:16px;color:#000000;">购物商城，“电商</span><span style="font-size:16px;color:#000000;">+</span><span style="font-size:16px;color:#000000;">店商”二合一网络交易平台。网络店铺和实体门店无缝对接，电子商务和本地生活完美融合。</span><span></span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">B2B2C电商平台集</span><b><span style="font-size:16px;color:#000000;">网络购物、本地资讯、信息查阅、活动组织与推广、便民服务</span></b><span style="font-size:16px;color:#000000;">等为一体，是新型的网络购物超市。</span><span></span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">相比国内购物平台，</span><b><span style="font-size:16px;color:#000000;">B2B2C电商平台拥有本土优势跟地域优势，能真正为本地用户提供优质服务</span><span style="font-size:16px;color:#000000;">!</span><span style="font-size:16px;color:#000000;">保障用户体验</span><span style="font-size:16px;color:#000000;">!</span></b> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">&nbsp;</span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-size:16px;color:#000000;">　　</span><span style="font-size:16px;color:#000000;">注：即日起B2B2C电商平台正式招商，首批商家入驻B2B2C电商平台</span><b><span style="font-size:16px;color:#000000;">零年费、零平台使用费。</span></b><span style="font-size:16px;color:#000000;">B2B2C电商平台全面启动用户、商家技术和服务支持体系，B2B2C电商平台为你开启电商财富之门。</span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">&nbsp;</span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-size:16px;color:#000000;">　　</span><b><span style="font-size:16px;color:#000000;">招商类目：</span><span></span></b> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">服饰鞋帽、礼品箱包、家居家装、数码办公、家用电器、个护化妆、珠宝手表、食品饮料、运动健康、汽车用品、玩具乐器、厨房用具、母婴用品、本地生活等类目。</span><span></span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">&nbsp;</span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-size:16px;color:#000000;">　　</span><b><span style="font-size:16px;color:#000000;">招商细则：</span><span></span></b> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-size:16px;color:#000000;">　　</span><span style="font-size:16px;color:#000000;">●</span><span style="font-size:16px;color:#000000;">一般商家需提供相关认证资质扫描件，审核通过，免费开店，特殊产品资质以我公司招商部需要的资料为准。</span><span></span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-size:16px;color:#000000;">　　</span><span style="font-size:16px;color:#000000;">●</span><span style="font-size:16px;color:#000000;">代理商需要提供代理授权资质：需要确保授权链条的完整，即申请入驻企业拿到的授权能够逐级逆推回品牌商</span><span></span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-size:16px;color:#000000;">　　</span><span style="font-size:16px;color:#000000;">●</span><span style="font-size:16px;color:#000000;">消费凭证：所有入驻的企业必须给消费者提供盖有公章的消费凭证，消费凭证盖章的公司名称必须和商家与B2B2C电商平台合作的公司名称一致</span><span></span> \r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-size:16px;color:#000000;">　　</span><span style="font-size:16px;color:#000000;">● 请务必保证您入驻前后提供的相关资质的真实有效性</span><span style="font-size:16px;color:#000000;">(</span><span style="font-size:16px;color:#000000;">若您提供的相关资质为第三方提供，如商标注册证、授权书等，请务必先行核实其真实有效性</span><span style="font-size:16px;color:#000000;">)</span><span style="font-size:16px;color:#000000;">，一旦发现虚假资质，您的公司将被列入非诚信商家名单，B2B2C电商平台将不再与您进</span><span style="font-size:16px;color:#000000;">行合作。</span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">&nbsp;</span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<b><span style="font-size:16px;color:#000000;">商家加盟咨询热线：</span><span></span></b> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">13800138000&nbsp; &nbsp; &nbsp;&nbsp;QQ：</span><span style="font-size:16px;color:#000000;">123456789</span>\r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	<span style="font-size:16px;color:#000000;">&nbsp;</span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:156.0pt;">\r\n	<span style="font-size:16px;color:#000000;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;B2B2C电商平台旗下 B2B2C电商平台</span><span></span> \r\n</p>\r\n<p class="MsoNormal" style="text-indent:192.0pt;">\r\n	<span style="font-size:16px;color:#000000;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span>\r\n</p>', 1419218570),
